"use strict";document.addEventListener("alpine:init",()=>{Alpine.data("teamGenerator",()=>({membersInput:"",numberOfTeams:2,generatedTeams:[],shuffleArray(a){for(let b=a.length-1;b>0;b--){const c=Math.floor(Math.random()*(b+1));[a[b],a[c]]=[a[c],a[b]]}return a},parseMembers(a){return a.split(",").map(b=>b.trim()).filter(b=>b!=="")},generateTeams(){this.generatedTeams=[];const a=this.parseMembers(this.membersInput);if(a.length===0)return;const b=this.shuffleArray([...a]);const c=Array.from({length:this.numberOfTeams},()=>[]);b.forEach((d,e)=>{const f=e%this.numberOfTeams;c[f].push(d)});this.generatedTeams=c}}))});